import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('WORKER');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:8080/api/auth/login', {
        email,
        password,
      });
      const user = res.data.user;
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(user));
      if (user.role === 'WORKER') {
        navigate('/worker/dashboard');
      } else {
        navigate('/contractor/dashboard');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen px-4">
      <div className="w-full max-w-md bg-dark-800 rounded-xl shadow-2xl p-8 border border-dark-700">
        <h2 className="text-3xl font-bold text-primary-400 mb-6 text-center">Job Portal Login</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-300">Email</label>
            <input
              type="email"
              className="mt-1 block w-full rounded-md bg-dark-700 border-dark-600 text-white shadow-sm focus:ring-primary-500 focus:border-primary-500"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-300">Password</label>
            <input
              type="password"
              className="mt-1 block w-full rounded-md bg-dark-700 border-dark-600 text-white shadow-sm focus:ring-primary-500 focus:border-primary-500"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-300">Role</label>
            <select
              className="mt-1 block w-full rounded-md bg-dark-700 border-dark-600 text-white shadow-sm focus:ring-primary-500 focus:border-primary-500"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="WORKER">Worker</option>
              <option value="CONTRACTOR">Contractor</option>
            </select>
          </div>
          {error && <div className="text-red-400 text-sm">{error}</div>}
          <button
            type="submit"
            className="w-full py-2 px-4 bg-gradient-to-r from-primary-600 to-secondary-600 hover:from-primary-700 hover:to-secondary-700 text-white font-semibold rounded-md shadow-lg transition-all duration-200"
          >
            Login
          </button>
        </form>
        <div className="mt-4 text-center text-sm text-gray-400">
          New user?{' '}
          <Link to="/register" className="text-primary-400 hover:text-primary-300 underline font-medium">
            Register here
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
