import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { User, Briefcase, MessageSquare, LogOut, Phone, MapPin, Star, Edit, X } from 'lucide-react';

const WorkerDashboard = () => {
  const [user, setUser] = useState(null);
  const [jobOffers, setJobOffers] = useState([]);
  const [activeTab, setActiveTab] = useState('profile');
  const [showEditModal, setShowEditModal] = useState(false);
  const [editData, setEditData] = useState({
    name: '',
    phone: '',
    location: '',
    skills: [],
    experience: '',
    dailyRate: '',
    description: '',
    availability: 'Available',
    available: true
  });
  const [newSkill, setNewSkill] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('user'));
    if (!userData || userData.role !== 'WORKER') {
      navigate('/login');
      return;
    }
    setUser(userData);
    fetchJobOffers(userData.id);
  }, [navigate]);

  const fetchJobOffers = async (workerId) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/job-offers/worker/${workerId}`);
      setJobOffers(response.data);
    } catch (error) {
      console.error('Error fetching job offers:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleJobOfferResponse = async (offerId, status) => {
    try {
      await axios.put(`http://localhost:8080/api/job-offers/${offerId}/status?status=${status}`);
      fetchJobOffers(user.id);
    } catch (error) {
      console.error('Error updating job offer:', error);
    }
  };

  const handleEditProfile = () => {
    setEditData({
      name: user.name,
      phone: user.phone,
      location: user.location || '',
      skills: user.skills || [],
      experience: user.experience || '',
      dailyRate: user.dailyRate || '',
      description: user.description || '',
      availability: user.availability || 'Available',
      available: user.available !== false
    });
    setShowEditModal(true);
  };

  const handleUpdateProfile = async () => {
    try {
      const response = await axios.put(`http://localhost:8080/api/workers/${user.id}`, editData);
      setUser(response.data);
      localStorage.setItem('user', JSON.stringify(response.data));
      setShowEditModal(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const addSkill = () => {
    if (newSkill.trim() && !editData.skills.includes(newSkill.trim())) {
      setEditData({
        ...editData,
        skills: [...editData.skills, newSkill.trim()]
      });
      setNewSkill('');
    }
  };

  const removeSkill = (skillToRemove) => {
    setEditData({
      ...editData,
      skills: editData.skills.filter(skill => skill !== skillToRemove)
    });
  };

  if (!user) return null;

  return (
    <div className="min-h-screen bg-dark-900">
      {/* Header */}
      <header className="bg-dark-800 border-b border-dark-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <h1 className="text-2xl font-bold text-primary-400">Worker Dashboard</h1>
            <div className="flex items-center space-x-4">
              <span className="text-gray-300">Welcome, {user.name}</span>
              <button
                onClick={handleLogout}
                className="flex items-center space-x-2 px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-md transition-colors"
              >
                <LogOut size={16} />
                <span>Logout</span>
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Navigation Tabs */}
        <div className="flex space-x-1 bg-dark-800 rounded-lg p-1 mb-8">
          <button
            onClick={() => setActiveTab('profile')}
            className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors ${
              activeTab === 'profile'
                ? 'bg-primary-600 text-white'
                : 'text-gray-400 hover:text-white'
            }`}
          >
            <User size={16} />
            <span>Profile</span>
          </button>
          <button
            onClick={() => setActiveTab('offers')}
            className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors ${
              activeTab === 'offers'
                ? 'bg-primary-600 text-white'
                : 'text-gray-400 hover:text-white'
            }`}
          >
            <Briefcase size={16} />
            <span>Job Offers</span>
          </button>
          <button
            onClick={() => setActiveTab('inbox')}
            className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors ${
              activeTab === 'inbox'
                ? 'bg-primary-600 text-white'
                : 'text-gray-400 hover:text-white'
            }`}
          >
            <MessageSquare size={16} />
            <span>Inbox</span>
          </button>
        </div>

        {/* Profile Tab */}
        {activeTab === 'profile' && (
          <div className="bg-dark-800 rounded-lg p-6 border border-dark-700">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-semibold text-primary-400">Profile Information</h2>
              <button
                onClick={handleEditProfile}
                className="flex items-center space-x-2 px-4 py-2 bg-primary-600 hover:bg-primary-700 text-white rounded-md transition-colors"
              >
                <Edit size={16} />
                <span>Edit Profile</span>
              </button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Name</label>
                <p className="text-white">{user.name}</p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Email</label>
                <p className="text-white">{user.email}</p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Phone</label>
                <p className="text-white flex items-center">
                  <Phone size={16} className="mr-2" />
                  {user.phone}
                </p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Location</label>
                <p className="text-white flex items-center">
                  <MapPin size={16} className="mr-2" />
                  {user.location || 'Not specified'}
                </p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Skills</label>
                <div className="flex flex-wrap gap-2">
                  {user.skills?.map((skill, index) => (
                    <span
                      key={index}
                      className="px-3 py-1 bg-primary-600 text-white text-sm rounded-full"
                    >
                      {skill}
                    </span>
                  ))}
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Daily Rate</label>
                <p className="text-white">₹{user.dailyRate || 'Not specified'}</p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Experience</label>
                <p className="text-white flex items-center">
                  <Star size={16} className="mr-2" />
                  {user.experience || 'Not specified'} years
                </p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Availability</label>
                <span className={`px-3 py-1 rounded-full text-sm ${
                  user.availability === 'Available' 
                    ? 'bg-green-600 text-white' 
                    : 'bg-yellow-600 text-white'
                }`}>
                  {user.availability}
                </span>
              </div>
            </div>
          </div>
        )}

        {/* Job Offers Tab */}
        {activeTab === 'offers' && (
          <div className="space-y-4">
            <h2 className="text-xl font-semibold text-primary-400 mb-6">Job Offers</h2>
            {jobOffers.length === 0 ? (
              <div className="bg-dark-800 rounded-lg p-8 text-center border border-dark-700">
                <Briefcase size={48} className="mx-auto text-gray-500 mb-4" />
                <p className="text-gray-400">No job offers yet</p>
              </div>
            ) : (
              jobOffers.map((offer) => (
                <div key={offer.id} className="bg-dark-800 rounded-lg p-6 border border-dark-700">
                  <div className="flex justify-between items-start mb-4">
                    <div>
                      <h3 className="text-lg font-semibold text-white">{offer.contractorName}</h3>
                      <p className="text-gray-400">{offer.jobDetails}</p>
                    </div>
                    <span className={`px-3 py-1 rounded-full text-sm ${
                      offer.status === 'PENDING' 
                        ? 'bg-yellow-600 text-white' 
                        : offer.status === 'ACCEPTED'
                        ? 'bg-green-600 text-white'
                        : 'bg-red-600 text-white'
                    }`}>
                      {offer.status}
                    </span>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-400">Location</label>
                      <p className="text-white">{offer.location}</p>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-400">Rate</label>
                      <p className="text-white">₹{offer.offeredRate}/day</p>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-400">Duration</label>
                      <p className="text-white">{offer.duration}</p>
                    </div>
                  </div>
                  <p className="text-gray-300 mb-4">{offer.message}</p>
                  {offer.status === 'PENDING' && (
                    <div className="flex space-x-4">
                      <button
                        onClick={() => handleJobOfferResponse(offer.id, 'ACCEPTED')}
                        className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-md transition-colors"
                      >
                        Accept
                      </button>
                      <button
                        onClick={() => handleJobOfferResponse(offer.id, 'REJECTED')}
                        className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-md transition-colors"
                      >
                        Decline
                      </button>
                    </div>
                  )}
                </div>
              ))
            )}
          </div>
        )}

        {/* Inbox Tab */}
        {activeTab === 'inbox' && (
          <div className="bg-dark-800 rounded-lg p-6 border border-dark-700">
            <h2 className="text-xl font-semibold text-primary-400 mb-6">Inbox</h2>
            <div className="space-y-4">
              {user.inbox?.length > 0 ? (
                user.inbox.map((message) => (
                  <div key={message.id} className="border-b border-dark-700 pb-4">
                    <div className="flex justify-between items-start mb-2">
                      <h3 className="font-semibold text-white">{message.fromUserName}</h3>
                      <span className="text-sm text-gray-400">
                        {new Date(message.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                    <p className="text-gray-300">{message.content}</p>
                  </div>
                ))
              ) : (
                <p className="text-gray-400 text-center py-8">No messages yet</p>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default WorkerDashboard;
